import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { doc, onSnapshot } from 'firebase/firestore';
import { db } from '../firebase-config';
import { Container, Row, Col, Button, Card, CardBody, Input, Table } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faClipboard, faFan, faCoins } from '@fortawesome/free-solid-svg-icons';

const RecordsPage = () => {
    const { roomCode } = useParams();
    const [players, setPlayers] = useState([]);
    const [selectedPlayer, setSelectedPlayer] = useState('');
    const [selectedKong, setSelectedKong] = useState('');
    const [roomExists, setRoomExists] = useState(true);

    useEffect(() => {
        if (!roomCode) return;
        const roomRef = doc(db, 'rooms', roomCode);

        const unsubscribe = onSnapshot(roomRef, (docSnap) => {
            if (docSnap.exists()) {
                setRoomExists(true);
                const sortedPlayers = docSnap.data().players.sort((a, b) => b.score - a.score);
                setPlayers(sortedPlayers);
                if (sortedPlayers.length > 0) {
                    setSelectedPlayer(sortedPlayers[0].uid);
                }
            } else {
                setRoomExists(false);
            }
        });

        return () => unsubscribe();
    }, [roomCode]);

    if (!roomExists) {
        return (
            <div className="records-background text-center py-5">
                <h2>Room Not Found</h2>
                <p>The room with code <strong>{roomCode}</strong> does not exist.</p>
            </div>
        );
    }

    const ActionCard = ({ title, children }) => (
        <Col md={4}>
            <div className="action-section">
                <h5 className="mb-3">{title}</h5>
                {children}
            </div>
        </Col>
    );

    return (
        <div className="records-background">
            <Container className="py-5">
                <div className="records-container p-4 rounded-4">
                    <h2 className="mb-4"><FontAwesomeIcon icon={faClipboard} /> Game Records</h2>
                    <Row className="mb-5">
                        <ActionCard title="胡 (Hu - Win)">
                            <Button className="btn-declare-win w-100">🎉 Declare Win</Button>
                        </ActionCard>
                        <ActionCard title="花 (Hua - Flower)">
                            <Input type="select" className="mb-2" value={selectedPlayer} onChange={e => setSelectedPlayer(e.target.value)}>
                                {players.map(p => <option key={p.uid} value={p.uid}>{p.wind} - {p.name}</option>)}
                            </Input>
                            <Button className="btn-record-action w-100">🌸 Record Flower</Button>
                        </ActionCard>
                        <ActionCard title="槓 (Gang - Kong)">
                             <Input type="select" className="mb-2" value={selectedKong} onChange={e => setSelectedKong(e.target.value)}>
                                <option>加槓 (Added Kong)</option>
                                <option>暗槓 (Concealed Kong)</option>
                                <option>明槓 (Melded Kong)</option>
                            </Input>
                            <Button className="btn-record-action w-100">■ Record Kong</Button>
                        </ActionCard>
                    </Row>

                    <Row>
                        <Col>
                            <div className="action-section">
                                <h5 className="mb-3">Other Actions</h5>
                                <Button className="btn-record-action"><FontAwesomeIcon icon={faUndo} /> Undo Last Action</Button>
                            </div>
                        </Col>
                    </Row>

                    <hr className="my-5" />

                    <div>
                        <h3 className="text-center mb-4"><FontAwesomeIcon icon={faCoins} /> Leaderboard</h3>
                        <Table borderless responsive className="text-center leaderboard-table">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Player</th>
                                    <th>Wind</th>
                                    <th>Score</th>
                                </tr>
                            </thead>
                            <tbody>
                                {players.map((player, index) => (
                                    <tr key={player.uid}>
                                        <td>{index + 1}</td>
                                        <td>{player.name}</td>
                                        <td>{player.wind}</td>
                                        <td>{player.score}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </div>
                </div>
            </Container>
        </div>
    );
};

export default RecordsPage;
