import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Button, Card, CardBody } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCrown, faSync, faStopCircle, faPlayCircle } from '@fortawesome/free-solid-svg-icons';


const GameMasterPage = () => {
    const { roomCode } = useParams();
    // Mock players data, replace with real data from Firebase later
    const [players, setPlayers] = useState([
        { id: 1, name: 'Player 1', score: 0, wind: 'East (東)' },
        { id: 2, name: 'Player 2', score: 0, wind: 'South (南)' },
        { id: 3, name: 'Player 3', score: 0, wind: 'West (西)' },
    ]);

    const canStartGame = players.length === 4;

    return (
        <div className="gm-background">
            <Container className="py-5">
                <h2 className="gm-header"><FontAwesomeIcon icon={faCrown} /> Game Master</h2>
                <div className="room-code-display my-4">
                    Room Code: {roomCode}
                </div>
                <Row>
                    <Col md={8}>
                        <h4 className="mb-3">Players in Room</h4>
                        {players.map(player => (
                            <Card key={player.id} className="player-card mb-3">
                                <CardBody className="text-center">
                                    <div className="wind-indicator">{player.wind}</div>
                                    <div className="player-name">{player.name}</div>
                                    <div className="player-score">{player.score}</div>
                                </CardBody>
                            </Card>
                        ))}
                         {players.length < 4 && (
                            <Card className="player-card mb-3">
                                <CardBody className="text-center d-flex align-items-center justify-content-center">
                                    <p className="text-muted">Waiting for more players...</p>
                                </CardBody>
                            </Card>
                        )}
                    </Col>
                    <Col md={4}>
                        <h4 className="mb-3">Game Controls</h4>
                        <Button className="w-100 mb-2 gm-btn" disabled={!canStartGame}>
                            <FontAwesomeIcon icon={faPlayCircle} /> Start New Round
                        </Button>
                        <Button className="w-100 mb-2 gm-btn-alt">
                            <FontAwesomeIcon icon={faSync} /> Reset All Scores
                        </Button>
                        <Button color="danger" className="w-100 gm-btn-danger">
                            <FontAwesomeIcon icon={faStopCircle} /> End Game
                        </Button>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default GameMasterPage;
