// client/src/pages/Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { doc, setDoc } from 'firebase/firestore';
import { db, auth } from '../firebase-config';
import {
  Container,
  Row,
  Col,
  Button,
  Input,
  Form,
  FormGroup,
} from 'reactstrap';

const Home = () => {
  const navigate = useNavigate();

  const handleCreateRoom = async () => {
    const user = auth.currentUser;
    if (!user) {
      // Should not happen due to ProtectedRoute, but as a safeguard
      console.error("No user is logged in.");
      return;
    }

    // Generate a random 4-digit code
    const randomCode = Math.floor(1000 + Math.random() * 9000);
    const roomCode = `MJNG${randomCode}`;

    const roomRef = doc(db, 'rooms', roomCode);

    try {
      // Create a new room document in Firestore
      await setDoc(roomRef, {
        roomCode: roomCode,
        players: [], // Start with an empty player list
        createdAt: new Date(),
        gameMaster: user.uid,
      });
      // Navigate to the Game Master page with the new room code
      navigate(`/gamemaster/${roomCode}`);
    } catch (error) {
      console.error("Error creating room: ", error);
      // Handle error, e.g., show a notification to the user
    }
  };

  return (
    <div className="home-background">
      <Container>
        <div className="home-container p-5 rounded-4">
          <h2 className="mb-4">üè† Home</h2>
          <Row>
            <Col md={6} className="d-flex flex-column">
              <div className="create-room-section">
                <h3>Create New Room</h3>
                <p>Start a new mahjong game session</p>
                <Button className="create-room-btn mt-auto" onClick={handleCreateRoom}>
                  + Create Room
                </Button>
              </div>
            </Col>
            <Col md={6}>
              <div className="join-room-section">
                <h3>Join Existing Room</h3>
                <Form>
                  <FormGroup>
                    <label htmlFor="roomCode">Room Code</label>
                    <Input
                      type="text"
                      name="roomCode"
                      id="roomCode"
                      placeholder="Enter room code"
                    />
                  </FormGroup>
                  <Button className="join-room-btn">üö™ Join Room</Button>
                </Form>
              </div>
            </Col>
          </Row>
        </div>
      </Container>
    </div>
  );
};

export default Home;
