import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { doc, onSnapshot, updateDoc, arrayUnion } from 'firebase/firestore';
import { db } from '../firebase-config';
import { useAuth } from '../hooks/useAuth';
import { Container, Row, Col, Button, Card, CardBody, Input, Table } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndo, faClipboard, faFan, faCoins, faChartBar } from '@fortawesome/free-solid-svg-icons';

const RecordsPage = () => {
    const { roomCode } = useParams();
    const { user: currentUser } = useAuth();
    const [players, setPlayers] = useState([]);
    const [selectedFlowerPlayer, setSelectedFlowerPlayer] = useState('');
    const [selectedKongType, setSelectedKongType] = useState('Concealed Kong');
    const [selectedKongTarget, setSelectedKongTarget] = useState('');
    const [room, setRoom] = useState(null);
    const [roomExists, setRoomExists] = useState(true);

    useEffect(() => {
        if (!roomCode) return;
        const roomRef = doc(db, 'rooms', roomCode);

        const unsubscribe = onSnapshot(roomRef, (docSnap) => {
            if (docSnap.exists()) {
                setRoomExists(true);
                const roomData = docSnap.data();
                setRoom(roomData);
                const sortedPlayers = roomData.players.sort((a, b) => a.wind.localeCompare(b.wind));
                setPlayers(sortedPlayers);

                const otherPlayers = sortedPlayers.filter(p => p.uid !== currentUser?.uid);
                if (sortedPlayers.length > 0) {
                    if (!selectedFlowerPlayer) setSelectedFlowerPlayer(sortedPlayers[0].uid);
                    if (!selectedKongTarget && otherPlayers.length > 0) setSelectedKongTarget(otherPlayers[0].uid);
                }
            } else {
                setRoomExists(false);
            }
        });

        return () => unsubscribe();
    }, [roomCode, currentUser, selectedFlowerPlayer]);

    const handleRecordFlower = async () => {
        if (!currentUser || !selectedFlowerPlayer || currentUser.uid === selectedFlowerPlayer) {
            console.log("Invalid flower action: Cannot select yourself.");
            return;
        }

        const actorIndex = room.players.findIndex(p => p.uid === currentUser.uid);
        const targetIndex = room.players.findIndex(p => p.uid === selectedFlowerPlayer);

        if (actorIndex === -1 || targetIndex === -1) return;

        const updatedPlayers = [...room.players];
        updatedPlayers[actorIndex].score += 1;
        updatedPlayers[targetIndex].score -= 1;

        const roomRef = doc(db, 'rooms', roomCode);
        await updateDoc(roomRef, {
            players: updatedPlayers,
            history: arrayUnion({
                type: 'flower',
                actor: currentUser.uid,
                target: selectedFlowerPlayer,
                timestamp: new Date(),
            })
        });
    };

    const handleRecordKong = async () => {
        if (!currentUser) return;

        const actorIndex = room.players.findIndex(p => p.uid === currentUser.uid);
        if (actorIndex === -1) return;

        const updatedPlayers = [...room.players];
        let historyEntry = {
            type: 'kong',
            kongType: selectedKongType,
            actor: currentUser.uid,
            timestamp: new Date(),
        };

        if (selectedKongType === 'Concealed Kong') {
            updatedPlayers[actorIndex].score += 1;
        } else { // Melded Kong
            const targetIndex = room.players.findIndex(p => p.uid === selectedKongTarget);
            if (targetIndex === -1) return;

            updatedPlayers[actorIndex].score += 1;
            updatedPlayers[targetIndex].score -= 1;
            historyEntry.target = selectedKongTarget;
        }

        const roomRef = doc(db, 'rooms', roomCode);
        await updateDoc(roomRef, {
            players: updatedPlayers,
            history: arrayUnion(historyEntry)
        });
    };

    if (!roomExists) {
        return (
            <div className="records-background text-center py-5">
                <h2>Room Not Found</h2>
                <p>The room with code <strong>{roomCode}</strong> does not exist.</p>
            </div>
        );
    }

    const ActionCard = ({ title, children }) => (
        <Col md={4}>
            <div className="action-section">
                <h5 className="mb-3">{title}</h5>
                {children}
            </div>
        </Col>
    );

    return (
        <div className="records-background">
            <Container className="py-5">
                <div className="records-container p-4 rounded-4">
                    <h2 className="mb-4"><FontAwesomeIcon icon={faClipboard} /> Game Records</h2>
                    <Row className="mb-5">
                        <ActionCard title="胡 (Hu - Win)">
                            <Button className="btn-declare-win w-100">🎉 Declare Win</Button>
                        </ActionCard>
                        <ActionCard title="花 (Hua - Flower)">
                            <Input type="select" className="mb-2" value={selectedFlowerPlayer} onChange={e => setSelectedFlowerPlayer(e.target.value)}>
                                {players.map(p => <option key={p.uid} value={p.uid}>{p.wind} - {p.name}</option>)}
                            </Input>
                            <Button className="btn-record-action w-100" onClick={handleRecordFlower}>🌸 Record Flower</Button>
                        </ActionCard>
                        <ActionCard title="槓 (Gang - Kong)">
                             <Input type="select" className="mb-2" value={selectedKongType} onChange={e => setSelectedKongType(e.target.value)}>
                                <option>Concealed Kong</option>
                                <option>Melded Kong</option>
                            </Input>
                            {selectedKongType === 'Melded Kong' && (
                                <Input type="select" className="mb-2" value={selectedKongTarget} onChange={e => setSelectedKongTarget(e.target.value)}>
                                    {players.filter(p => p.uid !== currentUser?.uid).map(p => <option key={p.uid} value={p.uid}>{p.wind} - {p.name}</option>)}
                                 </Input>
                            )}
                            <Button className="btn-record-action w-100" onClick={handleRecordKong}>■ Record Kong</Button>
                        </ActionCard>
                    </Row>

                    <Row>
                        <Col md={4}>
                            <div className="action-section">
                                <h5 className="mb-3">Other Actions</h5>
                                <Button className="btn-record-action"><FontAwesomeIcon icon={faUndo} /> Undo Last Action</Button>
                            </div>
                        </Col>
                         <Col md={8} className="text-md-end">
                             <div className="action-section">
                                <h5 className="mb-3">View Scores</h5>
                                <Link to={`/score/${roomCode}`} className="btn btn-declare-win">
                                    <FontAwesomeIcon icon={faChartBar} /> View Current Scores
                                </Link>
                            </div>
                        </Col>
                    </Row>

                    <hr className="my-5" />

                    <div>
                        <h3 className="text-center mb-4"><FontAwesomeIcon icon={faCoins} /> Leaderboard</h3>
                        <Table borderless responsive className="text-center leaderboard-table">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Player</th>
                                    <th>Wind</th>
                                    <th>Score</th>
                                </tr>
                            </thead>
                            <tbody>
                                {players.map((player, index) => (
                                    <tr key={player.uid}>
                                        <td>{index + 1}</td>
                                        <td>{player.name}</td>
                                        <td>{player.wind}</td>
                                        <td>{player.score}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </div>
                </div>
            </Container>
        </div>
    );
};

export default RecordsPage;
