// client/src/App.js
import React from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import { onAuthStateChanged } from 'firebase/auth';
import { auth } from './firebase-config';

// Page Components
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import Home from './pages/Home';
import GameMasterPage from './pages/GameMasterPage';
import ProfilePage from './pages/ProfilePage';
import RecordsPage from './pages/RecordsPage';
import ScorePage from './pages/ScorePage';

// Shared Components
import SideBar from './components/SideBar';
import ProtectedRoute from './components/ProtectedRoute'; // We'll create this

const App = () => {
  const [user, setUser] = React.useState(null);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
    return () => unsubscribe(); // Cleanup on unmount
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Or a proper spinner component
  }

  return (
    <div className="d-flex" style={{ minHeight: '100vh' }}>
      {user && <SideBar />}
      <div className="flex-grow-1" style={{ marginLeft: user ? '200px' : '0' }}>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />

          {/* Protected Routes */}
          <Route path="/" element={<ProtectedRoute user={user}><Home /></ProtectedRoute>} />
          <Route path="/gamemaster/:roomCode" element={<ProtectedRoute user={user}><GameMasterPage /></ProtectedRoute>} />
          <Route path="/profile" element={<ProtectedRoute user={user}><ProfilePage /></ProtectedRoute>} />
          <Route path="/records" element={<ProtectedRoute user={user}><RecordsPage /></ProtectedRoute>} />
          <Route path="/score" element={<ProtectedRoute user={user}><ScorePage /></ProtectedRoute>} />

          {/* Fallback route */}
          <Route path="*" element={<ProtectedRoute user={user}><Home /></ProtectedRoute>} />
        </Routes>
      </div>
    </div>
  );
};

export default App;
